import React from "react";
import { useCart, useDispatchCart } from "../components/ContextReducer";
import { Link } from "react-router-dom";
import { ChakraProvider, Box, Table, Thead, Tbody, Tr, Th, Td, Button, IconButton } from "@chakra-ui/react";
import { DeleteIcon } from "@chakra-ui/icons";

// Define the styles
const styles = {
  tableContainer: {
    maxHeight: '400px', // Adjust this value as needed
    overflowY: 'auto',
    overflowX: 'hidden'
  },
  buttonContainer: {
    textAlign: 'center'
  },
  sds: {
    zIndex: 1000
  }
};

export default function Cart() {
  let data = useCart();
  let dispatch = useDispatchCart();

  if (data.length === 0) {
    return (
      <div>
        <div className="m-5 w-100 text-center fs-3">The list is empty</div>
      </div>
    );
  }

  const handleCheckOut = async () => {
    let userEmail = localStorage.getItem("userEmail");
    let response = await fetch("http://localhost:5000/api/CareerData", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        career_data: data,
        email: userEmail,
        career_date: new Date().toDateString()
      })
    });

    console.log("JSON RESPONSE:::::", response.status);
    if (response.status === 200) {
      dispatch({ type: "DROP" });
    }
  };

  return (
    <ChakraProvider>
      <Box className="container m-auto mt-5" style={styles.sds}>
        <Box className="table-responsive table-responsive-sm table-responsive-md" style={styles.tableContainer}>
          <Table variant="striped" colorScheme="teal">
            <Thead>
              <Tr>
                <Th>#</Th>
                <Th>Name</Th>
                <Th>Things you should know</Th>
                <Th></Th>
              </Tr>
            </Thead>
            <Tbody>
              {data.map((career, index) => (
                <Tr key={index}>
                  <Td>{index + 1}</Td>
                  <Td>{career.name}</Td>
                  <Td>{career.skills}</Td>
                  <Td>
                    <IconButton
                      aria-label="Delete"
                      icon={<DeleteIcon />}
                      onClick={() => { dispatch({ type: "REMOVE", index: index }) }}
                    />
                  </Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
        </Box>
        <Box style={styles.buttonContainer}>
          <Link to="/myorder">
            <Button colorScheme="green" mt={4} onClick={handleCheckOut}>
              Click here to know the career path(s)
            </Button>
          </Link>
        </Box>
      </Box>
    </ChakraProvider>
  );
}
