import React from 'react';
import { CartProvider, useCart, useDispatchCart } from './ContextReducer'; // Adjust import path based on your file structure

const ShoppingCart = () => {
    const cartItems = useCart();
    const dispatch = useDispatchCart();

    const handleRemoveItem = (index) => {
        dispatch({ type: 'REMOVE', index });
    };

    return (
        <div>
            <h2>Shopping Cart</h2>
            <ul>
                {cartItems.map((item, index) => (
                    <li key={item.id}>
                        <div>{item.name}</div>
                        <button onClick={() => handleRemoveItem(index)}>Remove</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

const App = () => {
    return (
        <CartProvider>
            <div>
                <h1>My App</h1>
                <ShoppingCart />
            </div>
        </CartProvider>
    );
};

export default App;
